mapper.select:
select tb_formula.index_guid "indexGuid" , tb_formula.org_id "orgId", td_org_info.org_name "orgName", tb_formula.p_ymd "pYmd", tb_formula.p_year "pYear", tb_formula.start_time "startTime", tb_formula.end_time "endTime",
td_org_info.lxr "lxr", td_org_info.lxdh "lxdh", tb_formula.audit_fstate "auditFstate", (select count(*) from tb_formula_detail where index_guid = tb_formula.index_guid and index_value is not null) "indexValue", (select count(*) from tb_formula_template_detail) "indexCount" from tb_formula join td_org_info on (tb_formula.org_id = td_org_info.org_id) where tb_formula.qc_org_id = #{conditiions.orgId, jdbcType = DECIMAL}
and regexp_like(td_org_info.org_name, #{conditiions.orgName, jdbcType=VARCHAR})
and tb_formula.audit_fstate = #{conditiions.auditFstate, jdbcType=VARCHAR}
and tb_formula.p_year = #{conditiions.pYear, jdbcType=VARCHAR}

select staff_sum "staffSum" ,plan_bed_sum "planBedSum"
	,(select sum(meeting_dept_user_sum ) from tb_constr_dept_meeting
	where meeting_date > to_date(#{startDate},'yyyy-MM-dd')
	and meeting_date >= to_date(#{endDate},'yyyy-MM-dd')) "meetingDeptUserSum"
	from tb_constr_dept_org where 
	constr_dept_guid = 
	(select constr_dept_guid from 
	tb_constr_dept where org_id = #{orgId}
	and p_year = #{pYear}
	)

 		select * from v_qc_score where qc_org_id = #{qcOrgId} 
			and index_p_code = #{indexPCode}
			and org_id = #{orgId}
			order by ymd desc

 		select * from v_qc_score where qc_org_id = #{conditiions.qcOrgId, jdbcType=DECIMAL} 
			 and ymd <= #{conditiions.ymd, jdbcType=DECIMAL} 
				and index_p_code = #{conditiions.indexPCode, jdbcType=VARCHAR} 
			order by ymd desc
