mapper.select:
	select COUNT(1) from (

	    select TD_ORG_INFO.ORG_NAME from TD_ORG_INFO where TD_ORG_INFO.ORG_NAME = #{orgValue,jdbcType=VARCHAR}

			  and TD_ORG_INFO.ORG_ID not in ( #{orgId, jdbcType=DECIMAL} )



	    select TD_ORG_INFO.Org_Code from TD_ORG_INFO where TD_ORG_INFO.Org_Code = #{orgValue,jdbcType=VARCHAR}

			  and TD_ORG_INFO.ORG_ID not in ( #{orgId, jdbcType=DECIMAL} )


    )

select oi.ORG_ID "orgId",oi.Org_Type "orgType",oi.ORG_NAME "orgName", oi.Org_Alias "orgAlias",oi.Tf_Province "tfProvince",oi.Tf_City "tfCity",oi.Tf_District "tfDistrict", to_char(oi.Modify_Time,'yyyy-MM-dd') MODIFY_TIME, oi.ORG_CODE "orgCode",oi.TF_ACCESSORY "tfAccessory", oi.Lxr "lxr",oi.Lxdh "lxdh", oi.Tf_Province || oi.Tf_City || oi.Tf_District "orgAddress", oi.AUDIT_FSTATE "auditFstate", cdo.Hospital_Level "hospitalLevel",cdo.HOSPITAL_PROPERTY "hospitalProperty", cdo.p_year "pYear", qoi.org_name "qcOrgName", cdo.plan_bed_sum "planBedSum", cdo.actual_bed_sum "actualBedSum", cdo.staff_sum "staffSum" from TD_ORG_INFO oi left join TB_CONSTR_DEPT_ORG cdo on oi.ORG_ID = cdo.ORG_ID left join td_org_info qoi on cdo.qc_org_id = qoi.org_id
and oi.ORG_ID = #{conditiions.orgId, jdbcType=DECIMAL}
and oi.Org_Type = #{conditiions.orgType, jdbcType=VARCHAR}
and cdo.qc_org_id = #{conditiions.qcOrgId, jdbcType=DECIMAL}
and cdo.Hospital_Level = #{conditiions.hospitalLevel, jdbcType=VARCHAR}
and cdo.HOSPITAL_PROPERTY = #{conditiions.hospitalProperty, jdbcType=VARCHAR}
and cdo.p_year = #{conditiions.pYear, jdbcType=VARCHAR}
and cdo.plan_bed_sum
between 0 and 100
between 100 and 200
between 200 and 500
> 500
and cdo.staff_sum
between 0 and 100
between 100 and 200
between 200 and 500
> 500
and ( regexp_like(oi.ORG_NAME, #{conditiions.searchName,jdbcType=VARCHAR}) or regexp_like(oi.Org_Alias, #{conditiions.searchName,jdbcType=VARCHAR}) or regexp_like(oi.FQUN, #{conditiions.searchName,jdbcType=VARCHAR}))
order by cdo.p_year desc,oi.org_id

  	select TD_ORG_INFO.ORG_ID AS "value",TD_ORG_INFO.ORG_NAME AS "text" from TD_ORG_INFO where TD_ORG_INFO.Flag = '00'

	  	and TD_ORG_INFO.Org_Id not in (
		  select TD_ORG_INFO.ORG_ID from TD_ORG_INFO where TD_ORG_INFO.Org_Id = #{conditiions.orgId, jdbcType=DECIMAL} or TD_ORG_INFO.Parent_Orgid = #{conditiions.orgId, jdbcType=DECIMAL}
		)


	  	and regexp_like(TD_ORG_INFO.ORG_NAME, #{conditiions.searchName,jdbcType=VARCHAR})
